import React, { useState, useEffect } from 'react';
import "./"

function Login() {
  const [userWalletAddress, setUserWalletAddress] = useState(null);

  useEffect(() => {
    toggleButton();
  }, []);

  function toggleButton() {
    const loginButton = document.getElementById('loginButton');
    if (!window.ethereum) {
      loginButton.innerText = 'MetaMask is not installed';
      loginButton.classList.remove('bg-purple-500', 'text-white');
      loginButton.classList.add('bg-gray-500', 'text-gray-100', 'cursor-not-allowed');
      return false;
    }

    loginButton.addEventListener('click', loginWithMetaMask);
  }

  async function loginWithMetaMask() {
    try {
      const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
      setUserWalletAddress(accounts[0]);
      const userWallet = document.getElementById('userWallet');
      userWallet.innerText = accounts[0];
      const loginButton = document.getElementById('loginButton');
      loginButton.innerText = 'Sign out of MetaMask';
      loginButton.removeEventListener('click', loginWithMetaMask);
      setTimeout(() => {
        loginButton.addEventListener('click', signOutOfMetaMask);
      }, 200);
    } catch (error) {
      console.error(error.message);
    }
  }

  function signOutOfMetaMask() {
    setUserWalletAddress(null);
    const userWallet = document.getElementById('userWallet');
    userWallet.innerText = '';
    const loginButton = document.getElementById('loginButton');
    loginButton.innerText = 'Sign in with MetaMask';
    loginButton.removeEventListener('click', signOutOfMetaMask);
    setTimeout(() => {
      loginButton.addEventListener('click', loginWithMetaMask);
    }, 200);
  }

  return (
    <div className="flex w-screen h-screen justify-center items-center bg-gray-100">
      <div className="flex-col space-y-2 justify-center items-center bg-white p-8 rounded-lg shadow-lg">
        <button id='loginButton' className="rounded-md px-4 py-2 bg-purple-500 text-white hover:bg-purple-600 focus:outline-none focus:ring-2 focus:ring-purple-500 focus:ring-opacity-50">
          Login with MetaMask
        </button>
        <p id='userWallet' className='text-lg text-gray-600 my-2'>{userWalletAddress}</p>
      </div>
    </div>
  );
}

export default Login;
